import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {

  const refeicao = {
    nomeColaborador: 'Date',
    cpf: '',
    alimento: ''
  }

  const [resGet, setResGet] = useState(0)

  const teste = () => {
    setResGet(resGet +1)
  }

  const handleSubmit = event => {
    event.preventDefault()

    axios.get('https://unidac-api-rest.herokuapp.com/api/v1/colaborador')
      .then(res => {
        const retorno = res.data
        setResGet(retorno)
        console.log(retorno)
      })
  }

  return (
    <div className="App">
      <section className="secHome"></section>
      <section className="secForm" >
        <h1>Cadastre seu café da manhã</h1>
        <form className="formPost" onSubmit={handleSubmit}>
          <label>Nome:</label>
          <input type="text"></input>

          <label>CPF:</label>
          <input type="text"></input>

          <label>Refeição:</label>
          <input type="text"></input>

          <button onClick={teste} type="submit">Cadastrar</button>
        </form>
        <p>{resGet}</p>
        <h1>{resGet}</h1>
      </section>
    </div>
  );
}

export default App;
