import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {

  const [refeicao, setRefeicao] = useState({
    nomeColaborador: '',
    cpf: '',
    alimento: ''
  })

  const [resposta, setResposta] = useState(false)

  const [resGet, setResGet] = useState()

  const handleChangeNome = event => {
    setRefeicao({nomeColaborador: event.target.value})
  }

  const handleChangeCpf = event => {
    setRefeicao({cpf: event.target.value})
  }

  const handleChangeAlimento = event => {
    setRefeicao({alimento: event.target.value})
  }

  const handleSubmit = event => {
    event.preventDefault()

    axios.post('https://unidac-api-rest.herokuapp.com/api/v1/colaborador', refeicao)
      .then(res => {
        // setResGet(res.data)
        // setResposta(true)
        console.log(res)
      })
  }

  return (
    <div className="App">
      <section className="secHome"></section>
      <section className="secForm" >
        <h1>Cadastre seu café da manhã</h1>
        <form className="formPost" onSubmit={handleSubmit}>
          <label>Nome:</label>
          <input type="text" onChange={handleChangeNome}></input>

          <label>CPF:</label>
          <input type="text" onChange={handleChangeCpf}></input>

          <label>Refeição:</label>
          <input type="text" onChange={handleChangeAlimento}></input>

          <button  type="submit">Cadastrar</button>
        </form>
        {/* {resposta ? resGet.map(item => item.cpf) : ''} */}
        {}
      </section>
    </div>
  );
}

export default App;
