import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {

  const refeicao = {
    nomeColaborador: "Dante",
    cpf: '50434115894',
    alimento: 'Bananao'
  }

  const [resGet, setResGet] = useState(refeicao)

  const handleSubmit = event => {
    event.preventDefault()

    axios.get('https://unidac-api-rest.herokuapp.com/api/v1/colaborador')
      .then(res => {
        setResGet(res.data)
        console.log()
      })
  }

  return (
    <div className="App">
      <section className="secHome"></section>
      <section className="secForm" >
        <h1>Cadastre seu café da manhã</h1>
        <form className="formPost" onSubmit={handleSubmit}>
          <label>Nome:</label>
          <input type="text"></input>

          <label>CPF:</label>
          <input type="text"></input>

          <label>Refeição:</label>
          <input type="text"></input>

          <button type="submit">Cadastrar</button>
        </form>
        <p>{resGet.alimento}</p>
      </section>
    </div>
  );
}

export default App;
