import React from 'react';
import axios from 'axios';
import './App.css';

export default class App extends React.Component {

  state = {
    nomeColaborador: '',
    cpf: '',
    alimento: '',
    registros: []
  }



  handleChangeNome = event => {
    this.setState({ nomeColaborador: event.target.value })
  }

  handleChangeCpf = event => {
    this.setState({ cpf: event.target.value })
  }

  handleChangeAlimento = event => {
    this.setState({ alimento: event.target.value })
  }

  handleSubmit = event => {
    event.preventDefault()

    const cadastro = {
      nomeColaborador: this.state.nomeColaborador,
      cpf: this.state.cpf,
      alimento: this.state.alimento
    }

    axios.post('https://unidac-api-rest.herokuapp.com/api/v1/colaborador', cadastro)
      .then(res => {
        console.log(res.data);
        console.log(res)
      })
  }

  handleGet = event => {
    axios.get('https://unidac-api-rest.herokuapp.com/api/v1/colaborador')
    .then(res => {
      console.log(res.data);
      console.log(res)
      this.setState
    })
  }

  refeicoesCadastradas() {

    return (
      <li>
        <strong>Nome: {this.state.nomeColaborador}</strong>
        <strong>CPF: </strong>
        <strong>Refeição: </strong>
      </li>
    )
  }

  render() {

    return (
      <div className="App" >
        <section className="secHome"></section>
        <section className="secForm" >
          <form className="formPost" onSubmit={this.handleSubmit}>
            <h1>Cadastre seu café da manhã</h1>
            <label>Nome:</label>
            <input type="text" onChange={this.handleChangeNome}></input>

            <label>CPF:</label>
            <input type="text" onChange={this.handleChangeCpf}></input>

            <label>Refeição:</label>
            <input type="text" onChange={this.handleChangeAlimento}></input>

            <button type="submit">Cadastrar</button>
          </form>
        </section>
        <section className="secGet">
          <div>
            <h1>Cadastros registrados</h1>
            <ul>
              {this.refeicoesCadastradas()}
            </ul>
            <button className="btnAtualizar" onClick={this.handleGet}>Atualizar</button>
          </div>
        </section>
      </div>
    )
  }
}
